mapper.select:
SELECT * FROM board WHERE boardtype = #{boardtype}
AND content LIKE CONCAT('%', #{search}, '%')
AND subject LIKE CONCAT('%', #{search}, '%')
AND name LIKE CONCAT('%', #{search}, '%')
ORDER BY re_ref DESC, re_seq ASC LIMIT #{startRow}, #{pageSize}

mapper.update:

UPDATE board 
SET readcount = readcount + 1 
WHERE num = #{num}

UPDATE board 
SET name=#{name}, passwd=#{passwd}, subject=#{subject}, content=#{content}
WHERE num = #{num}

UPDATE board 
SET re_seq = re_seq + 1 
WHERE re_ref = #{reRef} 
AND re_seq = #{reSeq}

mapper.select:

SELECT * 
FROM board 
WHERE boardtype = #{boardtype} 
ORDER BY readcount DESC
LIMIT 0, #{pageSize}
